int GetNumberOfSets()
{
	return (sizeof gSetItems / sizeof gSetItems[0]) ;
}

CSetItems* GetSetItemRec( CString sCode) 
{
	for (int count=0; count<GetNumberOfSets(); count++) {
		for (int item=0; item < ITEMS_PER_SET; item++) {
//			if (sCode == gSetItems[ count].m_SetItems[ item].m_Code)
			CString sTmp = gSetItems[ count].m_SetItems[ item].m_Code ;
			if (sCode == sTmp)
				return &gSetItems[ count].m_SetItems[ item] ;
		}
	}

	return NULL ;
}
